import './App.css';
import {useQuery, gql} from '@apollo/client';

const query = gql`
  query GetTodos{
    getTodos {
      id
      title
      user{
        name
      }
    }
  }
`;


function App() {
  const {data, loading} = useQuery(query);
  if(loading) return <h1>loading ...</h1>
  return (
    <div className="App">
      {data}
    </div>
  );
}

export default App;
